      bs = BeautifulSoup(response.text, 'html5lib')

      courses = bs.select('div.course-area')
      courses = courses[0].select('div.course-filter-list')
      courses = courses[0].findAll('a')

      cat_1 = bs.select('div.breadcrumbs')[0].findAll('a')[1].text
